import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskTitle, setEditTaskTitle] = useState('');

  // Fetch tasks from API
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('There was an error fetching tasks!', error);
      }
    };
    fetchTasks();
  }, []);

  // Create new task
  const handleAddTask = async () => {
    if (!newTask) return; // Don't add empty tasks
    try {
      const response = await axios.post('http://localhost:5000/api/tasks', {
        title: newTask,
        completed: false,
      });
      setTasks([...tasks, response.data]);
      setNewTask('');
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // Handle task completion toggle
  const handleToggleComplete = async (taskId) => {
    try {
      const task = tasks.find((task) => task._id === taskId);
      const updatedTask = { ...task, completed: !task.completed };

      const response = await axios.put(`http://localhost:5000/api/tasks/${taskId}`, updatedTask);
      setTasks(tasks.map((task) => (task._id === taskId ? response.data : task)));
    } catch (error) {
      console.error('Error toggling task completion:', error);
    }
  };

  // Handle task editing
  const handleEditTask = (taskId, currentTitle) => {
    setEditTaskId(taskId);
    setEditTaskTitle(currentTitle);
  };

  // Save edited task
  const handleSaveEdit = async () => {
    if (!editTaskTitle) return; // Ensure title is not empty
    try {
      const updatedTask = { title: editTaskTitle, completed: false }; // You can also keep the current status of completed if needed
      const response = await axios.put(`http://localhost:5000/api/tasks/${editTaskId}`, updatedTask);
      
      setTasks(tasks.map((task) => (task._id === editTaskId ? response.data : task)));
      setEditTaskId(null); // Close the edit mode
      setEditTaskTitle(''); // Clear the input field
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditTaskId(null); // Close the edit mode
    setEditTaskTitle(''); // Clear the input field
  };

  return (
    <div className="App">
      <h1>To-Do List</h1>
      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a new task"
      />
      <button onClick={handleAddTask}>Add Task</button>

      {/* Task editing section */}
      {editTaskId && (
        <div>
          <input
            type="text"
            value={editTaskTitle}
            onChange={(e) => setEditTaskTitle(e.target.value)}
            placeholder="Edit task title"
          />
          <button onClick={handleSaveEdit}>Save</button>
          <button onClick={handleCancelEdit}>Cancel</button>
        </div>
      )}

      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            {task.title} {task.completed ? '(Completed)' : '(Pending)'}
            <button onClick={() => handleToggleComplete(task._id)}>
              Mark as {task.completed ? 'Pending' : 'Completed'}
            </button>
            <button onClick={() => handleEditTask(task._id, task.title)}>
              Edit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
